import React from "react";
import StartLog     from "./game_startLog";
import FinishLog    from "./game_finishLog";
import DamageLog    from "./game_damageLog";
import QuestionLog  from "./game_questionLog";
import AnswerLog    from "./game_answerLog";
import AnswerList   from "./game_answerList";


//props:gameParams, totalPhase
class Interview extends React.Component{
    constructor(props){
        super(props);
        this.state = {gamePhase:0};
        this.addGamePhase   = this.addGamePhase.bind(this);
    }

    addGamePhase(){
        this.setState({gamePhase:this.state.gamePhase+1});
    }

    render(){
        const CHARACTER = {
            image_src:this.props.gameParams.characters.image_src[0],
            name:this.props.gameParams.characters.name[0],
            background:this.props.gameParams.characters.background[0],
            skill:this.props.gameParams.characters.skill[0],
            personality:this.props.gameParams.characters.personality[0]
        }

        const COMPANY = {
            genre:this.props.gameParams.companies.genre[0],
            name:this.props.gameParams.companies.name[0]
        }

        const MEMBER = {
            image_src:this.props.gameParams.members.image_src[this.props.totalStep-1],
            name:this.props.gameParams.members.name[this.props.totalStep-1],
            HP:this.props.gameParams.members.HP[this.props.totalStep-1],
            personality:this.props.gameParams.members.personality[this.props.totalStep-1],
            position:this.props.gameParams.members.position[this.props.totalStep-1],
            question_group:this.props.gameParams.members.question_group[this.props.totalStep-1]
        }


        switch(this.state.gamePhase){
            case 0: return <StartLog character={CHARACTER} company={COMPANY} member={MEMBER} step={this.props.totalPhase} forChange={this.addGamePhase}/>;
                    break;
            case 1: return <QuestionLog　question={this.state.props.questions.text[2*this.props.totalPhase]} forChange={this.addGamePhase}/>; 
                    break;
            case 2: return <AnswerList forChange={this.addGamePhase}/>; break;
            case 3: return <AnswerLog forChange={this.addGamePhase}/>; break;
            case 4: return <DamageLog forChange={this.addGamePhase}/>; break;
            case 5: return <QuestionLog　question={this.state.props.questions.text[2*this.props.totalPhase+1]} forChange={this.addGamePhase}/>; 
                    break;
            case 6: return <AnswerList forChange={this.addGamePhase}/>; break;
            case 7: return <AnswerLog forChange={this.addGamePhase}/>; break;
            case 8: return <DamageLog forChange={this.addGamePhase}/>; break;
            case 9: return <FinishLog forChange={this.props.forChange}/>; break;
        }
    }
}

export default Interview;
